package main
 
import (
	"fmt"
	"net"
	"os"
)
 
func main() {
	addrs, err := net.LookupIP(os.Args[1])
	if err != nil {
		fmt.Println(err)
		os.Exit(1)
	}
 
	for _, addr := range addrs {
		fmt.Printf("addr: %s\n", addr)
	}
}
