This program should print 1, but cmd/compile rejects it with an "undefined: iota" error.

    package main
    
    import (
            "fmt"
            "unsafe"
    )
    
    const (
            _ = unsafe.Sizeof(func() int {
                    const (
                            _ = 1
                            _
                            _
                    )
                    return 0
            }())

            y = iota
    )

    func main() {
            fmt.Println(y)
    }
