func LinkSSH(host, user, pass string) (info string) {

	config := &ssh.ClientConfig{
		User: user,
		Auth: []ssh.AuthMethod{
			ssh.Password(pass),
		},
	}
	client, err := ssh.Dial("tcp", host, config)
	if err != nil {
                info = "connect error"
		return info
	} else {
		session, err := client.NewSession()
		defer session.Close()

		if err != nil {
                        info = "login error"
			return info
		} else {
			info = "login ok"
			client.Close()
			return info

		}
	}
}


